mapper.select:

select Q.QNA_NUM, Q.QNA_TITLE, Q.QNA_CONTENT, Q.QNA_REGDATE,Q.QNA_PROCESS,Q.QNACATE2,Q.QNACATE_NUM,C.QNACATE_NUM,C.QNACATE_NAME
from board_qna q join qna_category c
where Q.QNACATE_NUM = C.QNACATE_NUM
and USER_NUM = #{user_num}

order by QNA_NUM desc
limit #{pageStart}, #{perPageNum}


select Q.QNA_NUM, Q.QNA_TITLE, Q.QNA_CONTENT, Q.QNA_REGDATE,Q.QNA_PROCESS,Q.QNACATE2,Q.QNACATE_NUM,C.QNACATE_NUM,C.QNACATE_NAME
from board_qna q join qna_category c
on Q.QNACATE_NUM = C.QNACATE_NUM
where Q.USER_NUM = #{user_num}

order by QNA_NUM desc
limit #{pageStart}, #{perPageNum}


select qna_num, qna_title, qna_content, qna_regdate,qna_process,qna_image1,qna_image2,q.qnacate_num,c.qnacate_name
from board_qna q join qna_category c
where user_num = #{user_num}

order by qna_num desc
limit #{pageStart}, #{perPageNum}

select count(qna_num) 
from board_qna
where user_num = #{user_num}

select qnacom_content, admin_id, qnacom_regdate
from qna_comments c join admin a
where qna_num = #{qna_num}
